import React, { useEffect, useState } from "react";
import { fetchStreams, createStream } from "../api/api";
import StreamCard from "./StreamCard";

export default function StreamList() {
  const [streams, setStreams] = useState([]);
  const [name, setName] = useState("");
  const [source, setSource] = useState("");

  useEffect(() => {
    loadStreams();
  }, []);

  const loadStreams = async () => {
    const data = await fetchStreams();
    setStreams(data);
  };

  const handleAddStream = async () => {
    if (!name || !source) return alert("Enter both name and source");
    await createStream(name, source);
    setName(""); setSource("");
    loadStreams();
  };

  return (
    <div>
      <div style={{ marginBottom: 15 }}>
        <input placeholder="Stream Name" value={name} onChange={e => setName(e.target.value)} />
        <input placeholder="Source" value={source} onChange={e => setSource(e.target.value)} style={{ marginLeft: 5 }} />
        <button onClick={handleAddStream} style={{ marginLeft: 5 }}>Add Stream</button>
      </div>
      <div>
        {streams.map(stream => <StreamCard key={stream.id} stream={stream} />)}
      </div>
    </div>
  );
}
