import React, { useEffect, useState } from "react";
import { toggleModel, fetchResults } from "../api/api";

export default function StreamCard({ stream }) {
  const [results, setResults] = useState([]);

  useEffect(() => {
    loadResults();
  }, []);

  const loadResults = async () => {
    const res = await fetchResults(stream.id);
    setResults(res);
  };

  const handleToggle = async (modelName, enabled) => {
    await toggleModel(stream.id, modelName, enabled);
    setTimeout(() => loadResults(), 500); // wait for results
  };

  return (
    <div style={{ border: "1px solid #ddd", padding: 12, marginBottom: 10 }}>
      <h3>{stream.name}</h3>
      <p><strong>Source:</strong> {stream.source}</p>
      <div>
        <button onClick={() => handleToggle("road_detector", true)}>Enable Road Detector</button>
        <button onClick={() => handleToggle("road_detector", false)} style={{ marginLeft: 5 }}>Disable Road Detector</button>
        <button onClick={() => handleToggle("crack_classifier", true)} style={{ marginLeft: 5 }}>Enable Crack Classifier</button>
        <button onClick={() => handleToggle("crack_classifier", false)} style={{ marginLeft: 5 }}>Disable Crack Classifier</button>
      </div>
      <div style={{ marginTop: 10 }}>
        <strong>Recent Results:</strong>
        <pre style={{ maxHeight: 150, overflow: "auto" }}>{JSON.stringify(results, null, 2)}</pre>
      </div>
    </div>
  );
}
